mapper.select:
select s.* from contrast c, seller s , house h where c.c_id = #{c_id}
                                                and h.h_seller_id = c.c_house_id
                                                and	h.h_seller_id = s.s_id

mapper.update:
update contrast set

	 c_buyer_agree = #{c_buyer_agree}


	 c_seller_agree = #{c_seller_agree}

where c_id = #{c_id}

mapper.select:
select * from contrast where c_buyer_id = #{c_buyer_id}
                         and c_pay_way = #{c_pay_way}
                         and c_house_id = #{c_house_id}
                       order by c_id desc
                       limit 1
